package com.bgmsoft.shin.ui.main.recyclerview

import android.net.Uri
import android.os.Build
import androidx.lifecycle.ViewModelProviders
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.ItemTouchHelper
import androidx.recyclerview.widget.RecyclerView

import com.bgmsoft.shin.R
import com.bgmsoft.shin.model.Task
import kotlinx.android.synthetic.main.rv_example_fragment.*

class RvExampleFragment : Fragment() {

    companion object {
        fun newInstance() = RvExampleFragment()
    }

    private lateinit var viewModel: RvExampleViewModel

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.rv_example_fragment, container, false)
    }

    override fun onActivityCreated(savedInstanceState: Bundle?) {
        super.onActivityCreated(savedInstanceState)
        viewModel = ViewModelProviders.of(this).get(RvExampleViewModel::class.java)
        // TODO: Use the ViewModel

        // Dummy Data 설정
        val imageUrl: String =
            "data:image/png;base64,"
        for (i in 0..5) {
            viewModel.taskList.add(Task(1, "name $i", "detail detail", Uri.parse(imageUrl)))
        }

        initRecyclerView()
    }

    fun initRecyclerView() {
        // Recycler View에 Custom Adapter 설정
        rv_example_recyclerview.adapter = RvExampleDefaultAdapter()
        // Adapter 초기 데이터 설정
        (rv_example_recyclerview.adapter as RvExampleDefaultAdapter).setTaskList(viewModel.taskList)

        // ItemTouchHelper 구현 (SDK Version 22부터 사용 가능)
        ItemTouchHelper(object : ItemTouchHelper.SimpleCallback(0, ItemTouchHelper.LEFT or ItemTouchHelper.RIGHT) {
            override fun onMove(recyclerView: RecyclerView, viewHolder: RecyclerView.ViewHolder, target: RecyclerView.ViewHolder): Boolean {
                return true
            }

            override fun onSwiped(viewHolder: RecyclerView.ViewHolder, direction: Int) {
                // Adapter에 아이템 삭제 요청
                (rv_example_recyclerview.adapter as RvExampleDefaultAdapter).removeTask(viewHolder.adapterPosition)
            }

        }).apply {
            // ItemTouchHelper에 RecyclerView 설정
            attachToRecyclerView(rv_example_recyclerview)
        }
    }
}
